<?php

/**
 * This class has been auto-generated by the Doctrine ORM Framework
 */
abstract class BaseBan extends Doctrine_Record
{
	
  public function setTableDefinition()
  {
    $this->setTableName('ban');
    $this->hasColumn('id', 'integer', 4, array('type' => 'integer', 'length' => 4, 'primary' => true, 'autoincrement' => true));
    $this->hasColumn('type', 'integer', 1, array('type' => 'string', 'length' => 1, 'notnull' => true));
    $this->hasColumn('value', 'string', 255, array('type' => 'string', 'length' => 255, 'notnull' => true));
    $this->hasColumn('created_on', 'timestamp', null, array('type' => 'timestamp', 'notnull' => true));
  }
  
  /**
   * Is the item banned ? (email, 
   *
   * @param string $table
   * @param integer $type
   * @param string $value
   * @return boolean
   */
  protected static function isBanned($table,$type,$value) {
  	$q = new Doctrine_Query();
  	return $q	->from($table.' item')
  				->where('item.type=? AND item.value=?',array($type,$value))
  				->limit(1)
  				->count() == 1;
  				
  }
  
  /**
   * Creates a new ban entry.
   *
   * @param string $table
   * @param integer $type
   * @param string $value
   */
  protected static function ban($table,$type,$value) {
  	$record = Doctrine::getTable($table)->create();
  	$record->type = $type;
  	$record->value = $value;
  	$record->save();
  }
  
  /**
   * Removes the ban if any.
   *
   * @param string $table
   * @param integer $type
   * @param string $value
   */
  protected static function removeBan($table,$type,$value) {
  	$q = Doctrine::getTable($table)->createQuery();
  	$q	->where('type=? AND value=?',array($type,$value))
  		->limit(1)
  		->delete()
  		->execute();
  }
}